// Parse create-story output to extract story file path
const items = $input.all();
const results = [];

for (const item of items) {
  const stdout = item.json.stdout || '';
  const stderr = item.json.stderr || '';
  const exitCode = item.json.exitCode || 0;

  // Check for errors
  const isError = exitCode !== 0 || stderr.includes('Error:');

  // Extract story file path from output
  // Look for patterns like "Created: docs/sprint-artifacts/story-name.md"
  // or "Story file: docs/sprint-artifacts/story-name.md"
  const storyPathMatch = stdout.match(/(?:Created|Story file):\s*([\w\/\-\.]+\.md)/i);
  const storyPath = storyPathMatch ? storyPathMatch[1] : null;

  // Extract story title if present
  const titleMatch = stdout.match(/Story:\s*(.+?)(?:\n|$)/i);
  const storyTitle = titleMatch ? titleMatch[1].trim() : null;

  // Extract epic and story IDs if present
  const epicMatch = stdout.match(/Epic:\s*([\w\-\.]+)/i);
  const storyIdMatch = stdout.match(/Story ID:\s*([\w\-\.]+)/i);

  results.push({
    json: {
      success: !isError && storyPath !== null,
      exitCode: exitCode,
      storyPath: storyPath,
      storyTitle: storyTitle,
      epicId: epicMatch ? epicMatch[1] : null,
      storyId: storyIdMatch ? storyIdMatch[1] : null,
      output: stdout,
      error: isError ? (stderr || stdout) : null,
      executedAt: new Date().toISOString()
    }
  });
}

return results;
