// Store initial git status
const items = $input.all();
const results = [];

for (const item of items) {
  const stdout = item.json.stdout || '';
  const filesBefore = stdout.split('\n').filter(line => line.trim().length > 0);

  results.push({
    json: {
      ...item.json,
      gitStatusBefore: filesBefore
    }
  });
}

return results;

// Parse dev-story output and compare git status
const items = $input.all();
const results = [];

for (const item of items) {
  const devStoryOutput = $('Execute Dev Story').first().json;
  const gitStatusBefore = $('Store Before Status').first().json.gitStatusBefore || [];
  const gitStatusAfter = (item.json.stdout || '').split('\n').filter(line => line.trim().length > 0);

  // Determine success/failure from dev-story execution
  const exitCode = devStoryOutput.exitCode || 0;
  const stderr = devStoryOutput.stderr || '';
  const stdout = devStoryOutput.stdout || '';
  const isError = exitCode !== 0 || stderr.includes('Error:');

  // Find new/modified files
  const changedFiles = gitStatusAfter.filter(file => {
    return !gitStatusBefore.includes(file);
  }).map(line => {
    const parts = line.trim().split(/\s+/);
    return {
      status: parts[0],
      file: parts.slice(1).join(' ')
    };
  });

  // Extract story status from output
  const statusMatch = stdout.match(/Status:\s*(\w+)/i);
  const storyStatus = statusMatch ? statusMatch[1] : (isError ? 'failed' : 'completed');

  // Extract implementation details
  const tasksCompletedMatch = stdout.match(/Tasks completed:\s*(\d+)/i);
  const testsPassedMatch = stdout.match(/Tests passed:\s*(\d+)/i);

  results.push({
    json: {
      success: !isError,
      exitCode: exitCode,
      storyStatus: storyStatus,
      output: stdout,
      error: isError ? (stderr || stdout) : null,
      hasChanges: changedFiles.length > 0,
      changedFiles: changedFiles,
      tasksCompleted: tasksCompletedMatch ? parseInt(tasksCompletedMatch[1]) : null,
      testsPassed: testsPassedMatch ? parseInt(testsPassedMatch[1]) : null,
      gitStatusBefore: gitStatusBefore.length,
      gitStatusAfter: gitStatusAfter.length,
      executedAt: new Date().toISOString()
    }
  });
}

return results;
